	(91, 'purchase_order', 'purchase-request-approval', 'english', 'Request approval', '{type} Request approval', '<p><span> Hello {staff_name}! </span><br><br><span>You receive an approval request:</span></p>\r\n<table border="1" style="width: 100.077%;"><colgroup><col style="width: 25.8137%;"><col style="width: 74.1478%;"></colgroup>\r\n<tbody>\r\n<tr>\r\n<td><strong>Type</strong></td>\r\n<td>{type}</td>\r\n</tr>\r\n<tr>\r\n<td><strong>Requested by</strong></td>\r\n<td><span>{from_staff_name}</span></td>\r\n</tr>\r\n<tr>\r\n<td><strong>Link</strong></td>\r\n<td><span>{link}</span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><span>Please click the URL below to log in and view the details.</span></p>\r\n<p><span>Thank you.</span></p>', '{companyname} | ERP System', '', 0, 1, 0),
